</div>
<style>
    .global-wrap {
        height: 100vh;
    }
</style>
<script>
    $(document).ready(function() {
        //thêm thư viện select2
        $('.js-example-basic-single').select2();
        //thêm thư viện datepicker
        $( ".datepicker" ).datepicker();
        $(".btn-dat-ve").click(function(){
            $.ajax({
                //truyền lên router nào để xử lý
                url: '{{route("luuve")}}',
                type: 'POST',
                data: {
                    // dữ liệu truyền lên 
                    _token: '{{ csrf_token() }}',
                    gia: $('#gia').val(),
                    idchuyenbay: $('#idchuyenbay').val(),
                    loaive: $('#loaive').val(),
                    ngaydatve: $('#ngaydatve').val(),
                    tennguoidi: $('#tennguoidi').val(),
                    trangthai: $('#trangthai').val(),
                    vitringoi: $('#vitringoi').val(),
                },
                success: function (data) { 
                    alert('Đặt vé thành công!');
                },
                error: function(response) {
                    alert('Đặt vé thất bại!');
                }
            }); 
        });
    });
</script>

</body>

</html>